mapper.select:
select



from sp_cart t
where 1=1

    and t.cart_id = #{cartId}


    and t.user_id = #{userId}


    and t.status in ('O')
    and t.order_id = #{orderId}


    and t.status in ('I','S','G')

ORDER BY t.create_at DESC,t.sku_id

select



from sp_cart t
where 1=1 and t.status in ('I','G')

    and t.cart_id = #{cartId}


    and t.user_id = #{userId}


    and t.sku_id = #{skuId}


    and t.item_id = #{itemId}

ORDER BY t.create_at DESC,t.sku_id

mapper.update:
update sp_cart  set update_at= CURRENT_TIMESTAMP(0)

    ,user_id=#{userId}


    ,sku_id=#{skuId}


    ,item_id=#{itemId}


    ,amount=#{amount}


    ,price=#{price}


    ,destroy_at=CURRENT_TIMESTAMP(0)


    ,order_id=#{orderId}


    ,status=#{status}


    ,sku_title=#{skuTitle}

where 1=1

    and cart_id = #{cartId}


    and user_id = #{userId}


    and status in ('I','G')

mapper.select:
select
from sp_order t where 1=1
and t.user_id = #{userId}
and t.order_id = #{orderId}
and order_status !='N' and order_status !='PI' and order_status !='PS' order by t.order_create_at DESC

select
from sp_order t where 1=1
and t.user_id = #{userId}
and t.order_id = #{orderId}
and order_status !='N' order by t.order_create_at DESC

select



from sp_order t
where 1=1

    and t.user_id = #{userId}


    and t.order_id = #{orderId}


    and t.pin_active_id = #{pinActiveId}

and order_status ='PS'
order by t.order_create_at DESC

select



from sp_coupons t where 1=1

    and user_id = #{userId}


    and coup_id = #{coupId}


    and cate_id = #{cateId}


    and order_id = #{orderId}


    and state = #{state}


    and state in ('Y','N','S')


    and limit_quota = #{limitQuota}

and start_at <=CURRENT_TIMESTAMP(0) and end_at >=CURRENT_TIMESTAMP(0)

select



from sp_coupons t where 1=1

    and user_id = #{userId}


    and coup_id = #{coupId}


    and cate_id = #{cateId}


    and state = #{state}


    and state in ('Y','N','S')


    and limit_quota = #{limitQuota}

mapper.update:
update sp_coupons set coup_id = coup_id

    ,user_id = #{userId}


    ,cate_id = #{cateId}


    ,denomination = #{denomination}


    ,start_at = #{startAt}


    ,end_at = #{endAt}


    ,state =#{state}


    ,use_at=CURRENT_TIMESTAMP(0)


    ,order_id = #{orderId}

where 1= 1

    and coup_id = #{coupId}


    and user_id =#{userId}


    and cate_id =#{cateId}

update sp_coupons set coup_id = coup_id ,state ='S'

    ,user_id = #{userId}


    ,cate_id = #{cateId}


    ,denomination = #{denomination}


    ,start_at = #{startAt}


    ,end_at = #{endAt}


    ,use_at=#{useAt}

where 1= 1

    and coup_id = #{coupId}


    and user_id =#{userId}


    and state = #{state}


    and cate_id =#{cateId}

and end_at <=CURRENT_TIMESTAMP(0)

mapper.delete:
delete sp_coupons where 1=1

    and coup_id = #{coupId}


    and user_id =#{userId}


    and state = #{state}

mapper.select:
select split_id , order_id , state , cbe_code , insp_return_code , insp_return_msg , customs_return_code , customs_return_msg , total_fee , total_pay_fee , total_amount , ship_fee , postal_fee , express_num , express_code , express_nm from order_split where 1=1
and split_id = #{splitId}
and order_id = #{orderId}
and state = #{state}

select line_id, order_id , sku_id , item_id , amount , price , sku_title , sku_img , split_id , sku_size , sku_color , sku_type , sku_type_id, pin_tiered_price_id from sp_order_line where 1=1
and line_id=#{lineId}
and order_id=#{orderId}
and sku_id =#{skuId}
and item_id=#{itemId}
and split_id=#{splitId}
and sku_type=#{skuType}

select ship_id , order_id , delivery_name , delivery_tel , delivery_city , delivery_address , delivery_card_num from sp_order_ship where 1=1
and ship_id=#{shipId}
and order_id=#{orderId}

mapper.insert:
insert into sp_refund( order_id , pay_back_fee , reason , create_at , amount ,
refund_img ,
contact_name ,
contact_tel,
user_id , split_order_id , sku_id
,refund_type
)values( #{orderId}, #{payBackFee}, #{reason}, CURRENT_TIMESTAMP(0), #{amount},
#{refundImg} ,
#{contactName},
#{contactTel},
#{userId}, #{splitOrderId}, #{skuId}
,#{refundType}
)

update sp_refund set update_at = CURRENT_TIMESTAMP(0)

    ,refund_img = #{refundImg}


    ,pay_back_fee =  #{payBackFee}


    ,reason =  #{reason}


    ,amount =  #{amount}


    ,contact_name =  #{contactName}


    ,contact_tel =  #{contactTel}


    ,refund_type =  #{refundType}


    ,state =  #{state}

where 1=1

    and id = #{id}


    and user_id = #{userId}


    and split_order_id = #{splitOrderId}


    and sku_id = #{skuId}


    and order_id = #{orderId}

mapper.select:
select id , order_id , pay_back_fee , reason , state , create_at , pg_trade_no , update_at , pg_code , pg_message , amount , refund_img , contact_name , contact_tel , express_company , express_comp_code , express_num , reject_reason , user_id , split_order_id , sku_id, refund_type from sp_refund where 1=1
and id = #{id}
and user_id = #{userId}
and split_order_id = #{splitOrderId}
and sku_id = #{skuId}
and order_id = #{orderId}

select collect_id , user_id , sku_id , create_at , sku_type , sku_type_id from sp_collect where 1=1
and collect_id = #{collectId}
and user_id = #{userId}
and sku_id = #{skuId}

mapper.delete:
delete from sp_collect where 1=1

    and collect_id = #{collectId}


    and user_id =#{userId}
