mapper.select:
select



from sp_cart t
where 1=1

    and t.cart_id = #{cartId}


    and t.user_id = #{userId}


    and t.status in ('O')
    and t.order_id = #{orderId}


    and t.status in ('I','S','G')

ORDER BY t.create_at DESC,t.sku_id

select



from sp_cart t
where 1=1 and t.status in ('I','G')

    and t.cart_id = #{cartId}


    and t.user_id = #{userId}


    and t.sku_id = #{skuId}


    and t.item_id = #{itemId}


    and t.sku_type = #{skuType}


    and t.sku_type_id = #{skuTypeId}

ORDER BY t.create_at DESC,t.sku_id

mapper.update:
update sp_cart set update_at= CURRENT_TIMESTAMP(0)

    ,user_id=#{userId}


    ,sku_id=#{skuId}


    ,item_id=#{itemId}


    ,amount=#{amount}


    ,price=#{price}


    ,destroy_at=CURRENT_TIMESTAMP(0)


    ,order_id=#{orderId}


    ,status=#{status}


    ,sku_title=#{skuTitle}


    ,sku_type = #{skuType}


    ,sku_type_id = #{skuTypeId}

where 1=1

    and cart_id = #{cartId}


    and user_id = #{userId}


    and status in ('I','G')


    and sku_type = #{skuType}


    and sku_type_id = #{skuTypeId}

