mapper.select:
select



from sp_order t
where 1=1

    and t.user_id = #{userId}


    and t.order_id = #{orderId}


    and t.pg_trade_no = #{pgTradeNo}

and order_status !='N' and order_status !='PI' and order_status !='PS' and order_status !='PF'
order by t.order_create_at DESC

select



from sp_order t
where 1=1

    and t.user_id = #{userId}


    and t.order_id = #{orderId}


    and t.pg_trade_no = #{pgTradeNo}

and or_del =false
order by t.order_create_at DESC

