mapper.update:
update sp_coupons set coup_id = coup_id

    ,user_id = #{userId}


    ,cate_id = #{cateId}


    ,denomination = #{denomination}


    ,start_at = #{startAt}


    ,end_at = #{endAt}


    ,state =#{state}


    ,use_at=CURRENT_TIMESTAMP(0)


    ,order_id = #{orderId}

where 1= 1

    and coup_id = #{coupId}


    and user_id =#{userId}


    and cate_id =#{cateId}

update sp_coupons set coup_id = coup_id ,state ='S'

    ,user_id = #{userId}


    ,cate_id = #{cateId}


    ,denomination = #{denomination}


    ,start_at = #{startAt}


    ,end_at = #{endAt}


    ,use_at=#{useAt}

where 1= 1

    and coup_id = #{coupId}


    and user_id =#{userId}


    and state = #{state}


    and cate_id =#{cateId}

and end_at <=CURRENT_TIMESTAMP(0)

mapper.delete:
delete sp_coupons where 1=1

    and coup_id = #{coupId}


    and user_id =#{userId}


    and state = #{state}

mapper.update:
update order_split set

    order_id = #{orderId},


    state = #{state},


    cbe_code = #{cbeCode},


    insp_return_code = #{inspReturnCode},


    insp_return_msg = #{inspReturnMsg},


    customs_return_code=#{customsReturnCode},


    customs_return_msg = #{customsReturnMsg},


    total_fee = #{totalFee},


    total_pay_fee=#{totalPayFee},


    total_amount = #{totalAmount},


    ship_fee = #{shipFee},


    postal_fee = #{postalFee},


    express_num = #{expressNum},


    express_code = #{expressCode},


    express_nm = #{expressNm},

split_id = split_id where 1=1

    and split_id = #{splitId}


    and order_id = #{orderId}


    and state = #{state}


    and pay_insp_return_code = #{payInspReturnCode}


    and pay_insp_return_msg = #{payResponseMsg}


    and pay_customs_return_code = #{payCustomsReturnCode}


    and pay_customs_return_msg = #{payCustomsReturnMsg}


    and sub_pg_trade_no = #{subPgTradeNo}


    and pay_response_code = #{payResponseCode}


    and pay_response_msg = #{payResponseMsg}

mapper.select:
select split_id , order_id , state , cbe_code , insp_return_code , insp_return_msg , customs_return_code , customs_return_msg , total_fee , total_pay_fee , total_amount , ship_fee , postal_fee , express_num , express_code , express_nm, pay_insp_return_code, pay_insp_return_msg, pay_customs_return_code, pay_customs_return_msg, sub_pg_trade_no, pay_response_code, pay_response_msg from order_split where 1=1
and split_id = #{splitId}
and order_id = #{orderId}
and state = #{state}

select line_id,
order_id ,
sku_id ,
item_id ,
amount ,
price ,
sku_title ,
sku_img ,
split_id ,
sku_size ,
sku_color ,
sku_type ,
sku_type_id,
pin_tiered_price_id
from sp_order_line where 1=1

    and line_id=#{lineId}


    and order_id=#{orderId}


    and sku_id =#{skuId}


    and item_id=#{itemId}


    and split_id=#{splitId}


    and sku_type=#{skuType}


    and sku_type_id=#{skuTypeId}

