mapper.select:
select



from skus t where 1=1

    and sku_type=#{skuType}


    and sku_type_id=#{skuTypeId}


    and inv_id=#{invId}


    and sku_type_status=#{skuTypeStatus}

select



from inventories t where 1=1

    and t.id=#{id}


    and t.item_id=#{itemId}

mapper.update:
update inventories set update_at= CURRENT_TIMESTAMP(0)

    ,sold_amount=#{soldAmount}


    ,rest_amount=#{restAmount}


    ,state=#{state}

where 1=1

    and id=#{id}

mapper.select:
select



from sys_parameter t where 1=1

    and t.sys_id=#{sysId}


    and t.parameter_code=#{parameterCode}

SELECT COALESCE (cc.id, c.id) id, COALESCE (cc.add_fee,c.add_fee) add_fee, COALESCE (cc.add_num,c.add_num) add_num, COALESCE (cc.city_code,c.city_code) city_code, COALESCE (cc.first_fee,c.first_fee) first_fee, COALESCE (cc.first_num,c.first_num) first_num, COALESCE (cc.model_code,c.model_code) model_code, COALESCE (cc.model_name,c.model_name) model_name, COALESCE (cc.delivery_code,c.delivery_code) delivery_code, COALESCE (cc.delivery_name,c.delivery_name) delivery_name, COALESCE (cc.store_area,c.store_area) store_area FROM carriage c LEFT JOIN (SELECT c1.id, c1.add_fee, c1.add_num, c1.city_code, c1.first_fee, c1.first_num, c1.model_code, c1.model_name, c1.delivery_code, c1.delivery_name, c1.store_area FROM carriage c1 WHERE 1=1
and c1.city_code = #{cityCode}
and c1.city_code = 'ALL'
) cc on cc.store_area=c.store_area where 1=1
and c.store_area = #{storeArea}
and c.city_code = 'ALL'

select



from versioning t where 1=1

    and product_type=#{productType}


    and id=#{id}

order by current_version DESC,release_number DESC
