mapper.select:
select



from inventories t where 1=1

    and t.id=#{id}


    and t.item_id=#{itemId}

mapper.update:
update inventories set update_at= CURRENT_TIMESTAMP(0)

    ,t.sold_amount=#{soldAmount}


    ,t.rest_amount=#{restAmount}


    ,t.or_sold_out=#{orSoldOut}

where 1=1

    and t.id=#{id}

mapper.select:
select



from sys_parameter t where 1=1

    and t.sys_id=#{sysId}


    and t.parameter_code=#{parameterCode}

SELECT COALESCE (cc.id, c.id) id, COALESCE (cc.add_fee,c.add_fee) add_fee, COALESCE (cc.add_num,c.add_num) add_num, COALESCE (cc.city_code,c.city_code) city_code, COALESCE (cc.first_fee,c.first_fee) first_fee, COALESCE (cc.first_num,c.first_num) first_num, COALESCE (cc.model_code,c.model_code) model_code, COALESCE (cc.model_name,c.model_name) model_name FROM carriage c LEFT JOIN (SELECT c1.id, c1.add_fee, c1.add_num, c1.city_code, c1.first_fee, c1.first_num, c1.model_code, c1.model_name FROM carriage c1 WHERE 1=1
and c1.city_code = #{cityCode}
) cc on cc.model_code=c.model_code where 1=1
and c.model_code = #{modelCode}
AND c.city_code = 'ALL'
