mapper.select:
select



from inventories t where 1=1

    and t.id=#{id}


    and t.item_id=#{itemId}

mapper.update:
update inventories set update_at= CURRENT_TIMESTAMP(0)

    ,sold_amount=#{soldAmount}


    ,rest_amount=#{restAmount}


    ,state=#{state}

where 1=1

    and id=#{id}

mapper.select:
select



from sys_parameter t where 1=1

    and t.sys_id=#{sysId}


    and t.parameter_code=#{parameterCode}

SELECT c1.id, c1.add_fee, c1.add_num, c1.city_code, c1.first_fee, c1.first_num, c1.model_code, c1.model_name FROM carriage c1 WHERE 1=1
and c1.city_code = #{cityCode}
and c1.city_code = 'ALL'
and c1.model_code = #{modelCode}
